import { Component, OnInit } from '@angular/core';
import { ArtistDetails } from 'src/models/artist-details';
import { ArtistsSearchDataService } from 'src/shared/artists-search-data.service';
import { ArtistDetailsDataService } from 'src/shared/artist-details-data.service';
import { ArtistArtworksDataService } from 'src/shared/artist-artworks-data.service';
import { Artwork } from 'src/models/artwork';
import { Artist } from 'src/models/artist';

@Component({
  selector: 'app-artist-result-list',
  templateUrl: './artist-result-list.component.html',
  styleUrls: ['./artist-result-list.component.css']
})
export class ArtistResultListComponent implements OnInit {
  public chosen!: any
  public artists!: Array<Artist>
  public artistDetails!: ArtistDetails
  public artistArtworks!: ArtistDetails
  public infoLoaded: boolean = false
  public showSpinner: boolean = false

  constructor(
    private artistSearchData: ArtistsSearchDataService, 
    private artistDetailsData: ArtistDetailsDataService, 
    private artistArtworksData: ArtistArtworksDataService) {}

  ngOnInit(): void {
    this.artistSearchData.message.subscribe(data => this.artists = data)
  }

  private toggleSpinner() {
    this.showSpinner = !this.showSpinner
  }

  public async getArtistDetails(query: string) {
    const config = [
      `https://artistsearchbackend.wl.r.appspot.com/artists?id=${query}`,
      { method: 'GET', headers: { 'Content-Type': 'application/json' } },  
    ] as const
    return await fetch(...config)
      .then(response => { return response.json() })
      .then(parsed => new ArtistDetails(parsed))
  }

  public async getArtistArtworks(query: string) {
    const config = [
      `https://artistsearchbackend.wl.r.appspot.com/artworks?id=${query}`,
      { method: 'GET', headers: { 'Content-Type': 'application/json' } },  
    ] as const
    return await fetch(...config)
      .then(response => { return response.json() })
      .then(parsed =>parsed.map((item: Object) => { return new Artwork(item) }))
  }

  public async expandArtist(artistID: string) {
    console.log(this.infoLoaded)
    if (this.infoLoaded) { // clear details
      this.artistDetailsData.set([new ArtistDetails({}), !this.infoLoaded])
      this.artistArtworksData.set([new Artwork({}), !this.infoLoaded]) // render artworks
    } else this.infoLoaded = true
    this.toggleSpinner()
    this.artistDetails = await this.getArtistDetails(artistID)
    this.artistArtworks = await this.getArtistArtworks(artistID)
    this.toggleSpinner()
    this.artistDetailsData.set([this.artistDetails, this.infoLoaded]) // render details
    this.artistArtworksData.set([this.artistArtworks, this.infoLoaded]) // render artworks
  }
}
<div class="card-group artist-result-list d-flex mx-auto w-75 flex-row flex-nowrap pb-3 mb-1">
    <button class="card artist-card" [style.background-color]="(chosen == i) ? '#112B3C' : '#205375'" *ngFor="let artist of artists; let i = index;" (click)="chosen = i; expandArtist(artist.id)">
        <img class="card-img-top" src="{{ artist.image }}" alt="Card image cap">
        <div class="card-body pt-1 pb-0">
            <p class="card-title text-white text-left fs-6">{{ artist.name }}</p>
        </div>
    </button>
</div>
<div class="alert alert-danger w-75 mx-auto" role="alert">No results.</div>
<div class="row mt-2">
    <div class="spinner-border mx-auto" role="status" [hidden]="!showSpinner"></div>      
</div>
